INSERT INTO `db_product` (`id`, `catid`, `name`, `alias`, `avatar`, `img`, `sortDesc`, `detail`, `producer`, `number`, `number_buy`, `sale`, `price`, `price_sale`, `created`, `created_by`, `modified`, `modified_by`, `trash`, `status`) VALUES
(1, 2, 'Samsung Galaxy S10+ (Plus) Green', 'samsung-galaxy-s10-plus-green', '5df5496a8fb411fd207b39eae9802655.jpg', '5a6fb8ec2db3063e1e241a3e75d4e3ee.png', 'Samsung Galaxy S10 Plus Green chính hãng, nhiều ưu đãi khi đặt trước', '<h3><strong>Thiết kế thi&ecirc;n hướng&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-9-chinh-hang.html\" target=\"_blank\">Note 9</a>, kiểu m&agrave;n h&igrave;nh Infinity-O độc đ&aacute;o</strong></h3>\r\n\r\n<p>B&agrave;i to&aacute;n về hiển thị full m&agrave;n h&igrave;nh đ&atilde; được Samsung ho&agrave;n th&agrave;nh xuất sắc tr&ecirc;n Samsung Galaxy S10 Plus. Với thiết kế Infinity-O, m&agrave;n h&igrave;nh 6.4 inch tr&ecirc;n Galaxy S10 Plus đ&atilde; gần như chiếm trọn bộ mặt trước, với viền tr&ecirc;n v&agrave; cằm ở dưới rất mỏng. Tai thỏ, cũng như phần viền d&agrave;y đ&atilde; biến mất, thay v&agrave;o đ&oacute; l&agrave; một &lsquo;nốt ruồi&rsquo; chứa 2 camera selfie nằm ở g&oacute;c phải m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Phần nốt ruồi n&agrave;y được Samsung thiết kế rất tỉ mỉ, gọn g&agrave;ng, kh&ocirc;ng g&acirc;y qu&aacute; nhiều sự ch&uacute; &yacute;. B&ecirc;n cạnh đ&oacute;, nh&agrave; sản xuất H&agrave;n Quốc cũng kỹ lưỡng c&agrave;i đặt sẵn tr&ecirc;n m&aacute;y những h&igrave;nh nền mặc định &lsquo;tối dần&rsquo; về ph&iacute;a g&oacute;c tr&aacute;i, nhằm gi&uacute;p thiết kế tr&ocirc;ng liền lạc hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-s10-plus-chinh-hang-2.jpg.jpg\" style=\"height:525px; width:840px\" /></p>\r\n\r\n<h2><strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10plus-chinh-hang.html\" target=\"_blank\">Samsung Galaxy S10 Plus</a>&nbsp;ch&iacute;nh h&atilde;ng, nhiều ưu đ&atilde;i khi đặt trước</strong></h2>\r\n\r\n<p><em>Vậy l&agrave;&nbsp;<strong><a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-s.html\" target=\"_blank\">Samsung Galaxy S&nbsp;</a></strong>thế hệ thứ 10 đ&atilde; được ra mắt, v&agrave; kh&ocirc;ng l&agrave;m người d&ugrave;ng thất vọng một ch&uacute;t n&agrave;o. Trong bộ ba si&ecirc;u phẩm d&ograve;ng S năm nay,<strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10plus-chinh-hang.html\" target=\"_blank\">Samsung Galaxy S10 Plus</a>&nbsp;</strong>l&agrave; thiết bị đắt gi&aacute; cũng như được trang bị những t&iacute;nh năng đ&igrave;nh đ&aacute;m nhất từ&nbsp;<strong><a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">Samsung</a></strong>. Đ&acirc;y hứa hẹn sẽ trở th&agrave;nh một trong những smartphone ho&agrave;n hảo nhất năm 2019. Ngo&agrave;i ra, c&aacute;c si&ecirc;u phẩm dự kiến tr&igrave;nh l&agrave;ng của Samsung như&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s10e.html\" target=\"_blank\"><strong>Samsung Galaxy S10e</strong></a>,&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-chinh-hang.html\" target=\"_blank\"><strong>Samsung Galaxy Note 10&nbsp;</strong></a>với những thiết kế c&ugrave;ng t&iacute;nh năng đột ph&aacute; hứa hẹn sẽ b&ugrave;ng nổ giới c&ocirc;ng nghệ trong năm nay.&nbsp;</em></p>\r\n\r\n<h3><strong>Thiết kế thi&ecirc;n hướng&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-9-chinh-hang.html\" target=\"_blank\">Note 9</a>, kiểu m&agrave;n h&igrave;nh Infinity-O độc đ&aacute;o</strong></h3>\r\n\r\n<p>B&agrave;i to&aacute;n về hiển thị full m&agrave;n h&igrave;nh đ&atilde; được Samsung ho&agrave;n th&agrave;nh xuất sắc tr&ecirc;n Samsung Galaxy S10 Plus. Với thiết kế Infinity-O, m&agrave;n h&igrave;nh 6.4 inch tr&ecirc;n Galaxy S10 Plus đ&atilde; gần như chiếm trọn bộ mặt trước, với viền tr&ecirc;n v&agrave; cằm ở dưới rất mỏng. Tai thỏ, cũng như phần viền d&agrave;y đ&atilde; biến mất, thay v&agrave;o đ&oacute; l&agrave; một &lsquo;nốt ruồi&rsquo; chứa 2 camera selfie nằm ở g&oacute;c phải m&agrave;n h&igrave;nh.</p>\r\n\r\n<p>Phần nốt ruồi n&agrave;y được Samsung thiết kế rất tỉ mỉ, gọn g&agrave;ng, kh&ocirc;ng g&acirc;y qu&aacute; nhiều sự ch&uacute; &yacute;. B&ecirc;n cạnh đ&oacute;, nh&agrave; sản xuất H&agrave;n Quốc cũng kỹ lưỡng c&agrave;i đặt sẵn tr&ecirc;n m&aacute;y những h&igrave;nh nền mặc định &lsquo;tối dần&rsquo; về ph&iacute;a g&oacute;c tr&aacute;i, nhằm gi&uacute;p thiết kế tr&ocirc;ng liền lạc hơn.</p>\r\n\r\n<p><img alt=\"thiết kế samsung galaxy s10 plus\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-s10-plus-chinh-hang-1.jpg_1.jpg\" /></p>\r\n\r\n<p>Về thiết kế mặt lưng, tr&ecirc;n Galaxy S10 Plus, thay v&igrave; đặt camera nằm dọc, Samsung đ&atilde; xoay ngang cụm camera giống như tr&ecirc;n Note 9. Đồng thời v&acirc;n tay cũng đ&atilde; được t&iacute;ch hợp thẳng v&agrave;o trong m&agrave;n h&igrave;nh. Nhờ đ&oacute; m&agrave; thiết kế tr&ecirc;n m&aacute;y tr&ocirc;ng gọn g&agrave;ng, vu&ocirc;ng vứt hơn. Song song với đ&oacute;, Galaxy S10 Plus cũng c&oacute; độ mỏng rất ấn tượng, chỉ 7.8mm v&agrave; c&oacute; hai khối lượng dựa v&agrave;o tuỳ phi&ecirc;n bản: 175g (bản thường), 198g (bản gốm).</p>\r\n\r\n<p><img alt=\"màn hình samsung galaxy s10\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-s10-plus-chinh-hang-2.jpg.jpg\" /></p>\r\n\r\n<p>Samsung cũng kh&ocirc;ng qu&ecirc;n mang l&ecirc;n S10 Plus khả năng kh&aacute;ng nước IP68 cũng như mặt k&iacute;nh bảo vệ Gorilla Glass 5 ở ph&iacute;a sau. Về m&agrave;u sắc, Samsung Galaxy S10 Plus sở hữu rất nhiều phi&ecirc;n bản m&agrave;u sắc như: Prism White, Prism Black, Prism Green, Prism Blue, Canary Yellow, Flamingo Pink, Ceramic Black, Ceramic White. Nhưng hiện tại bạn c&oacute; thể đặt h&agrave;ng trước c&aacute;c m&agrave;u như Prism White, Prism Black v&agrave; Prism Green.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh Dynamic AMOLED 2K, t&iacute;ch hợp cảm biến v&acirc;n tay si&ecirc;u &acirc;m&nbsp;</strong></h3>\r\n\r\n<p>Tr&ecirc;n m&agrave;n h&igrave;nh Galaxy S10 Plus, nếu như thiết kế Infinity-O l&agrave; một điểm c&aacute;ch t&acirc;n, th&igrave; chất lượng hiển thị lại l&agrave; một lời khẳng định về chất lượng. M&agrave;n h&igrave;nh 6.4 inch tr&ecirc;n Galaxy S10 Plus được trang bị tấm nền Dynamic AMOLED do ch&iacute;nh Samsung ph&aacute;t triển. Với kỹ thuật Dynamic Tone Mapping, m&agrave;n h&igrave;nh sử dụng tấm nền tr&ecirc;n sẽ hỗ trợ tối ưu độ tương phản cho từng khung h&igrave;nh kh&aacute;c nhau. Song song với đ&oacute;, Dynamic AMOLED cũng đạt chuẩn HDR10+. Đi k&egrave;m với độ ph&acirc;n giải rất cao - 1440 x 3040, m&agrave;n h&igrave;nh tr&ecirc;n Galaxy S10 Plus chắc chắn sẽ mang đến những trải nghiệm hiển thị xuất sắc chưa từng c&oacute;.<img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-s10-plus-chinh-hang-4.jpg\" style=\"height:473px; width:840px\" /></p>\r\n\r\n<h3><strong>Hiệu năng mạnh mẽ với Exynos 9820, dung lượng RAM 8GB tối thiểu</strong></h3>\r\n\r\n<p>Galaxy S10 Plus được Samsung n&acirc;ng cấp l&ecirc;n vi xử l&yacute; Exynos 9820. Đ&acirc;y l&agrave; một CPU được thiết kế tr&ecirc;n tiến tr&igrave;nh 8nm v&agrave; chắc chắn sẽ mang đến một tốc độ xử l&yacute; nhanh hơn thế hệ cũ 9810 rất nhiều. Nh&acirc;n NPU cũng xuất hiện nhằm hỗ trợ c&aacute;c thuật to&aacute;n xử l&yacute; AI. B&ecirc;n cạnh đ&oacute;, mức ti&ecirc;u thụ pin của bộ vi xử l&yacute; n&agrave;y cũng &iacute;t hơn đ&aacute;ng kể. Song song với vi xử l&yacute; mạnh mẽ, Samsung cũng trang bị cho Galaxy S10 Plus bộ nhớ RAM tối thiểu l&agrave; 8GB (cho hai phi&ecirc;n bản 128<strong>/<a href=\"https://cellphones.com.vn/samsung-galaxy-s10-plus-512gb.html\" target=\"_blank\">512GB</a></strong>&nbsp;bộ nhớ trong). Đặc biệt hơn, bản tối đa cấu h&igrave;nh&nbsp;<strong><a href=\"https://cellphones.com.vn/samsung-galaxy-s10-plus-1tb.html\" target=\"_blank\">Galaxy S10 Plus 1TB</a></strong>&nbsp;sẽ sở hữu dung lượng RAM l&ecirc;n đến 12GB.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-s10-plus-chinh-hang-5.jpg\" style=\"height:531px; width:840px\" /></p>\r\n', 1, 320, 62, 10, 18290000, 15290000, '2019-05-22 16:22:43', '4', '2021-06-27 17:11:20', '1', 0, 1),
(2, 1, 'Apple iPhone XR 128GB Black Chính hãng', 'apple-iphone-xr-128gb-black-chinh-hang', '1c8f01e59cf56a2c2e946b58f64f7624.jpg', 'b6a64d0316b06e976ad4324a4063e341.jpg#13b9fc538c72f97683304fcd8c7194fb.jpg#2aa38bbfc27b9aac9d958fb4898bbe08.png', 'Sức mạnh đầu bảng thế giới smartphone', '<p><strong>Bạn cần mua một chiếc iPhone thời thượng, cấu h&igrave;nh mạnh mẽ v&agrave; camera xuất sắc nhất trong tầm gi&aacute; hợp l&yacute;. iPhone XR 128GB ch&iacute;nh l&agrave; lựa chọn d&agrave;nh cho bạn.</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-16.jpg\" style=\"height:534px; width:800px\" /></strong>Bộ vi xử l&yacute; Apple A12 Bionic tr&ecirc;n iPhone XR 128GB đang l&agrave; con chip mạnh mẽ nhất thế giới smartphone hiện nay. Bạn sẽ c&oacute; 6 nh&acirc;n, trong đ&oacute; 2 nh&acirc;n hiệu năng cao nhanh hơn 15% v&agrave; 4 nh&acirc;n tiết kiệm điện tiết kiệm hơn 50% so với thế hệ trước A11 Bionic của iPhone X. Đồng thời GPU đồ họa của A12 Bionic mạnh hơn tới 50%, cho ph&eacute;p bạn chơi mọi tựa game ở cấu h&igrave;nh cao nhất một c&aacute;ch nhẹ nh&agrave;ng v&agrave; mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-12.jpg\" style=\"height:533px; width:800px\" />B&ecirc;n cạnh sức mạnh đ&aacute;ng kinh ngạc, Apple A12 Bionic c&ograve;n l&agrave; con chip hỗ trợ tr&iacute; tuệ nh&acirc;n tạo. Với 8 nh&acirc;n Neural Engine c&oacute; khả năng học hỏi theo thời gian thực, iPhone XR c&oacute; thể nhận ra th&oacute;i quen sử dụng điện thoại của bạn, sau đ&oacute; đưa ra những dự đo&aacute;n, h&agrave;nh động xử l&yacute; nhanh dựa tr&ecirc;n những g&igrave; bạn l&agrave;m từng ng&agrave;y. 8 nh&acirc;n AI ri&ecirc;ng biệt c&ograve;n hỗ trợ cho iPhone XR trong rất nhiều trường hợp, từ nhận diện khu&ocirc;n mặt, chụp ảnh x&oacute;a ph&ocirc;ng, smart HDR cho đến c&ocirc;ng nghệ thực tế tăng cường AR. Mọi thứ đều trở n&ecirc;n dễ d&agrave;ng với bạn tr&ecirc;n iPhone XR.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-2.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<h3><strong>Camera chỉ một l&agrave; đủ</strong></h3>\r\n\r\n<p>Kh&ocirc;ng cần qu&aacute; nhiều camera sau mang t&iacute;nh chạy đua, iPhone XR 128GB vẫn nổi bật khi sở hữu cảm biến camera xuất sắc h&agrave;ng đầu. Camera ch&iacute;nh tr&ecirc;n iPhone XR c&oacute; độ ph&acirc;n giải 12MP, khẩu độ f/1.8, lấy n&eacute;t nhanh gấp đ&ocirc;i, chụp đ&ecirc;m sắc n&eacute;t v&agrave; c&oacute; khả năng chụp ảnh x&oacute;a ph&ocirc;ng ưu việt. T&iacute;nh năng nổi bật nhất l&agrave; Smart HDR, chụp đẹp trong mọi điều kiện &aacute;nh s&aacute;ng nhờ khả năng b&ugrave; trừ s&aacute;ng ho&agrave;n hảo. iPhone XR kh&ocirc;ng chỉ chụp ảnh x&oacute;a ph&ocirc;ng ch&iacute;nh x&aacute;c m&agrave; c&ograve;n c&oacute; thể điều chỉnh được độ mờ của hậu cảnh, th&ecirc;m c&aacute;c hiệu ứng để ảnh ch&acirc;n dung của bạn đẹp một c&aacute;ch chuy&ecirc;n nghiệp.</p>\r\n\r\n<h3><strong>Chiếc iPhone m&agrave;n h&igrave;nh lớn tr&agrave;n viền cạnh</strong></h3>\r\n\r\n<p>Đ&uacute;ng như đặc trưng của d&ograve;ng iPhone X, iPhone XR 128GB sử dụng thiết kế tr&agrave;n viền gi&uacute;p cho d&ugrave; m&agrave;n h&igrave;nh lớn tới 6,1 inch nhưng iPhone XR vẫn nhỏ gọn hơn so với iPhone 8 Plus, dễ d&agrave;ng cầm nắm thao t&aacute;c bằng một tay. M&agrave;n h&igrave;nh iPhone XR c&ocirc;ng nghệ Liquid Retina sắc n&eacute;t, m&agrave;u sắc ch&iacute;nh x&aacute;c, độ s&aacute;ng cao, t&aacute;i tạo cuộc sống v&ocirc; c&ugrave;ng ch&acirc;n thực. Đ&acirc;y l&agrave; m&agrave;n h&igrave;nh LCD đẹp nhất thế giới smartphone với 6 tấm cảm biến đ&egrave;n nền dưới m&agrave;n h&igrave;nh, tự động điều chỉnh c&acirc;n bằng trắng th&ocirc;ng minh để thay đổi nhiệt độ m&agrave;u dựa theo m&ocirc;i trường, gi&uacute;p bạn lu&ocirc;n được tận hưởng m&agrave;n h&igrave;nh đẹp v&agrave; dễ chịu cho mắt.<img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-17.jpg\" style=\"height:583px; width:800px\" /></p>\r\n\r\n<h3><strong>Thoải m&aacute;i lựa chọn m&agrave;u sắc theo &yacute; th&iacute;ch</strong></h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; kiểu d&aacute;ng đẹp mắt khi được ho&agrave;n thiện từ nh&ocirc;m v&agrave; vỏ k&iacute;nh bền nhất trong thế giới smartphone, iPhone XR c&ograve;n l&agrave; chiếc iPhone nhiều lựa chọn m&agrave;u sắc nhất. Rất nhiều m&agrave;u sắc đang chờ bạn lựa chọn: Đỏ, v&agrave;ng, trắng, hồng, đen, xanh. Đội ngũ thiết kế của Apple đ&atilde; d&ugrave;ng 7 tấm nền m&agrave;u sắc để m&agrave;u sơn c&oacute; độ s&acirc;u đầy ấn tượng phản chiếu qua lớp k&iacute;nh sang trọng. Vỏ k&iacute;nh cũng mang lại cho iPhone XR khả năng sạc kh&ocirc;ng d&acirc;y. Đặc biệt, iPhone XR c&ograve;n rất bền vững nhờ khả năng chống nước, mang tới sự y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<h3><strong>Thời lượng pin tuyệt vời</strong></h3>\r\n\r\n<p>Một trong những l&yacute; do để iPhone XR được y&ecirc;u th&iacute;ch ch&iacute;nh l&agrave; thời lượng pin. Đ&acirc;y l&agrave; chiếc iPhone c&oacute; thời lượng pin tốt nhất hiện nay, đảm bảo cho bạn sử dụng thoải m&aacute;i trong thời gian l&ecirc;n tới hai ng&agrave;y. C&ocirc;ng việc v&agrave; giải tr&iacute; lu&ocirc;n lu&ocirc;n tr&ocirc;i chảy với một chiếc điện thoại hiệu năng mạnh, pin tốt như iPhone XR.</p>\r\n\r\n<h3><strong>N&acirc;ng tầm bảo mật khu&ocirc;n mặt</strong></h3>\r\n\r\n<p>Nhận diện khu&ocirc;n mặt Face ID tr&ecirc;n iPhone XR đ&atilde; được n&acirc;ng l&ecirc;n tầm cao mới. Bạn c&oacute; thể mở kh&oacute;a iPhone, đăng nhập v&agrave;o ứng dụng hay thanh to&aacute;n chỉ trong nh&aacute;y mắt m&agrave; kh&ocirc;ng cần nhập mật khẩu. Đơn giản chỉ l&agrave; cầm m&aacute;y l&ecirc;n, điện thoại sẽ qu&eacute;t khu&ocirc;n mặt si&ecirc;u nhanh v&agrave; x&aacute;c thực mở kh&oacute;a ngay lập tức.<img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-11.jpg\" style=\"height:533px; width:800px\" /></p>\r\n', 1, 20, 26, 20, 21000000, 20000000, '2019-05-26 23:28:34', '1', '2021-06-27 17:08:04', '1', 0, 1),
(4, 2, 'Samsung Galaxy M20 Blue', 'samsung-galaxy-m20-blue', 'fa97ee4b0f27dcf536795b32c58ae1c0.jpg', '23fd459c94ccc1d5f5092169543ff3c2.jpg', 'Điện thoại Samsung Galaxy M20 - Cấu hình mạnh mẽ, thiết kế màn hình giọt nước', '<p><em>Nối tiếp th&agrave;nh c&ocirc;ng sau series&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-a.html\" target=\"_blank\">Galaxy A</a>,<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\">h&atilde;ng điện thoại SamSung</a>&nbsp;sẽ&nbsp;<em>ra mắt d&ograve;ng Galaxy M.</em><em>Theo đ&oacute;, Galaxy M10, Galaxy M20 l&agrave; 2 mẫu điện thoại đầu ti&ecirc;n được ra mắt v&agrave; sẽ được cập nhật&nbsp;<a href=\"https://www.android.com/versions/pie-9-0/\" target=\"_blank\">Android Pie 9</a>&nbsp;v&agrave;o th&aacute;ng 8/2018.</em></em></p>\r\n\r\n<h3><strong><a href=\"https://www.lifewire.com/what-is-tft-lcd-578664\" target=\"_blank\">M&agrave;n h&igrave;nh&nbsp;TFT LCD</a>&nbsp;tr&agrave;n viền, thiết kế&nbsp;giọt nước độc đ&aacute;o cho tỷ lệ hiển thị cao</strong></h3>\r\n\r\n<p>Tuy với chất liệu nhựa nhưng&nbsp;<strong>Samsung Galaxy M20&nbsp;</strong>vẫn mang trong m&igrave;nh một thiết kế nguy&ecirc;n khối sang trọng như một thiết bị cao cấp. Khi kh&ocirc;ng sờ v&agrave;o, mặt lưng b&oacute;ng bẩy cho cảm gi&aacute;c như&nbsp; mặt lưng k&iacute;nh sang trọng.</p>\r\n\r\n<p>Phần mặt trước được thiết kế với mặt k&iacute;nh cong 2,5 D. Sau thiết kế mang h&igrave;nh đục lỗ tr&ecirc;n&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a8-2018-chinh-hang.html\" target=\"_blank\">Samsung Galaxy A8</a>, th&igrave; M20 đ&atilde; quay trở lại với thiết kế giọt nước. Với k&iacute;ch thước 6.3 inch được thiết kế giọt nước c&ugrave;ng m&agrave;n h&igrave;nh tr&agrave;n viền đ&atilde; mang đến cho tỉ lệ hiển thị của Samsung Galaxy M20 l&ecirc;n tới gần 100%.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-m20-hinh-1.jpg\" style=\"height:592px; width:840px\" /></p>\r\n\r\n<p>Được trang bị c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;TFT LCD c&oacute; độ ph&acirc;n giải m&agrave;n h&igrave;nh fullHD+ 1080 x 2340 pixels, 16 triệu m&agrave;u. Nhờ đ&oacute; m&agrave;n h&igrave;nh Samsung Galaxy M20 cho khả năng hiển thị tốt với c&aacute;c icon kh&ocirc;ng bị vỡ, h&igrave;nh ảnh tươi s&aacute;ng.</p>\r\n\r\n<h3><strong>Điện thoại Samsung Galaxy M20 c&oacute; hiệu năng mạnh mẽ</strong></h3>\r\n\r\n<p>SamSung đ&atilde; trang bị cho sản phẩm của m&igrave;nh con chip tự sản xuất Exynos 7904 8 nh&acirc;n 64-bit, trong đ&oacute; 2 nh&acirc;n 2.2 GHz Cortex-A73 &amp; 6 nh&acirc;n 1.6 GHz Cortex-A53 tr&ecirc;n nền tảng hệ hiều h&agrave;nh Android 8.1 (Oreo). Nhờ đ&oacute; m&agrave;&nbsp;<strong>Samsung Galaxy M20</strong>c&oacute; một hiệu năng mạnh mẽ, c&aacute;c thao t&aacute;c tr&ecirc;n ứng dụng trơn tru.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-m20-hinh-2.jpg\" style=\"height:585px; width:840px\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Samsung Galaxy M20 c&ograve;n được trang bị con chip GPU Mali G71 cho khả năng chơi game tốt, đặc biệt l&agrave; những game đồ họa hay c&aacute;c tựa game đ&igrave;nh đ&aacute;m hiện nay như PUBG Mobile hoặc Asphalt 8.</p>\r\n\r\n<p>Với dung lượng RAM 3 GB, bộ nhớ trong 32 GB n&ecirc;n M20 cho khả năng hoạt động mượt m&agrave;, hạn chế t&igrave;nh trạng reload khi mở nhiều ứng dụng. Như vậy M20 hứa hẹn đ&aacute;p ứng được nhu cầu của những kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh nhất.</p>\r\n\r\n<h3><strong>Samsung Galaxy M20 sở hữu camera k&eacute;p cho chất lượng ảnh sắc n&eacute;t&nbsp;</strong></h3>\r\n\r\n<p>Galaxy M20 được trang bị cụm camera k&eacute;p với độ ph&acirc;n giải lần lượt l&agrave; 13 MP (khẩu độ F/1.9) + 5 MP (khẩu độ F/2.2) c&ugrave;ng nhiều t&iacute;nh năng chụp ảnh n&acirc;ng cao như chụp ảnh x&oacute;a ph&ocirc;ng, HDR, chạm nấy n&eacute;t. Ngo&agrave;i ra camera 5 MP c&ograve;n c&oacute; khả năng chụp h&igrave;nh với g&oacute;c si&ecirc;u rộng lần đầu ti&ecirc;n được trang bị tr&ecirc;n sản phẩm smartphone phổ th&ocirc;ng. C&oacute; lẽ SamSung đ&atilde; d&agrave;nh rất nhiều ưu &aacute;i cho Galaxy M20 khi sản phẩm n&agrave;y lại tiếp tục sở hữu một t&iacute;nh năng lần đầu ti&ecirc;n xuất hiện tr&ecirc;n c&aacute;c sản phẩm smartphone tầm trung. Với t&iacute;nh năng cao cấp n&agrave;y người d&ugrave;ng c&oacute; thể tha hồ lưu lại những khoảnh khắc trong cuộc sống h&agrave;ng ng&agrave;y hoặc c&oacute; thể tự tạo cho m&igrave;nh những bộ ảnh hết sức tuyệt vời.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-galaxy-m20-hinh-5.jpg\" style=\"height:544px; width:840px\" /></p>\r\n\r\n<h3>Mua điện thoại Samsung Galaxy M20 ở đ&acirc;u? Gi&aacute; bao nhi&ecirc;u?</h3>\r\n\r\n<p>Một sản phẩm tầm trung nhưng mang cấu h&igrave;nh mạnh mẽ,&nbsp;<strong>Samsung Galaxy M20</strong>&nbsp;hứa hẹn l&agrave; một đối thủ đ&aacute;ng gờm trong thị trường smartphone tầm trung, cạnh tranh trực tiếp với c&aacute;c t&ecirc;n tuổi Trung Quốc như Oppo, Huawei trong năm 2019. Nếu bạn quan t&acirc;m h&atilde;y đến ngay hệ thống cửa h&agrave;ng CellphoneS hoặc truy cập ngay webite để tham khảo gi&aacute; b&aacute;n chi tiết của sản phẩm n&agrave;y.</p>\r\n', 2, 400, 44, 10, 4990000, 4250000, '2019-06-03 21:45:31', '1', '2021-06-27 17:12:15', '1', 0, 1),
(5, 2, 'Samsung Galaxy A50 White', 'samsung-galaxy-a50-white', '636872943529900358_samsung-galaxy-a50-trang-1.png', '2803038d1e70c78f962564f440b52282.jpg#63f7ee76f51d47c525d4f3813ee9dc29.jpg', 'Điện thoại Samsung Galaxy A50 – Smartphone với màn hình tràn viền, cụm 3 camera ấn tượng', '<p><em>Sau&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a20.html\" target=\"_blank\"><strong>Galaxy A20</strong></a>&nbsp;v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-a30-64gb.html\" target=\"_blank\"><strong>Galaxy A30</strong></a>&nbsp;th&igrave; mới đ&acirc;y&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung.html\" target=\"_blank\"><strong>SamSung</strong></a>&nbsp;tiếp tục bổ sung một c&aacute;i t&ecirc;n mới v&agrave;o danh s&aacute;ch&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-a.html\" target=\"_blank\"><strong>Galaxy A</strong></a>&nbsp;của m&igrave;nh, đ&oacute; ch&iacute;nh l&agrave;&nbsp;<strong>SamSung Galaxy A50</strong>. A50 với nhiều c&ocirc;ng nghệ với v&agrave; hấp dẫn như cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh, cụm 3 camera hứa hẹn sẽ mang đến trải nghiệm tuyệt vời cho người d&ugrave;ng.</em></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh 6.4 inch &ndash; tận hưởng trọn vẹn với m&agrave;n h&igrave;nh v&ocirc; cực</strong></h3>\r\n\r\n<p>Galaxy A50 sở hữu một m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước lớn l&ecirc;n đến 6.4 inch c&ugrave;ng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Infinity-U tr&agrave;n viền mang đến những trải nghiệm tận c&ugrave;ng cho người d&ugrave;ng. C&ugrave;ng với đ&oacute; l&agrave; tấm nền&nbsp;<a href=\"https://www.lifewire.com/super-amoled-4151073\" target=\"_self\">Super AMOLED</a>&nbsp;chuẩn FHD+ v&agrave; m&agrave;n h&igrave;nh 16 triệu m&agrave;u nhờ đ&oacute; A50 mang đến những trải nghiệm xem ho&agrave;n to&agrave;n kh&aacute;c biệt với những h&igrave;nh ảnh sắc n&eacute;t, ch&acirc;n thực v&agrave; sống động đến từng chi tiết.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Galaxy-A50-1.jpg\" style=\"height:469px; width:840px\" /></p>\r\n\r\n<p>B&ecirc;n cạnh một m&agrave;n h&igrave;nh v&ocirc; cực với chất lượng hiển thị tốt th&igrave; Galaxy A50 c&ograve;n sở hữu một thiết kế ấn tượng với mặt k&iacute;nh 3D cao cấp kết hợp viền m&agrave;n h&igrave;nh cong tr&agrave;n tinh tế tạo n&ecirc;n một tổng thể nguy&ecirc;n khối liền mạch ho&agrave;n hảo nhờ đ&oacute; m&aacute;y cho khả năng cầm nắm kh&aacute; chắc chắn. Ngo&agrave;i ra, m&agrave;u sắc cũng l&agrave; một điểm nhấn ấn tượng tr&ecirc;n A50 khi m&aacute;y sở hữu nhiều phi&ecirc;n bản m&agrave;u độc đ&aacute;o&nbsp; như đen thi&ecirc;n thạch, xanh đại dương v&agrave; trắng ngọc trai.</p>\r\n\r\n<h3><strong>Bộ ba camera sau với khả năng chụp ảnh AI cho những bức ảnh chuẩn nhiếp ảnh gia</strong></h3>\r\n\r\n<p>Với ba ổng k&iacute;nh đa khẩu độ, từ camera g&oacute;c si&ecirc;u rộng 8MP mang đến g&oacute;c chụp 123 độ thật như mắt nh&igrave;n đến camera đo chiều s&acirc;u 5MP v&agrave; camera khẩu độ lớn 25MP thu s&aacute;ng vượt trội. Ngo&agrave;i ra, cụm camera sau tr&ecirc;n Galaxy A50 c&ograve;n được t&iacute;ch hợp nhiều t&iacute;nh năng th&ocirc;ng minh như chế độ tối ưu h&oacute;a cảnh gi&uacute;p nhận diện chủ thể. Hay c&ocirc;ng nghệ tr&iacute; th&ocirc;ng minh nh&acirc;n tạo AI gi&uacute;p tự động tối ưu độ s&aacute;ng, độ b&atilde;o h&ograve;a gi&uacute;p bức ảnh lu&ocirc;n đẹp ho&agrave;n hảo.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Galaxy-A50-2.jpg\" style=\"height:473px; width:840px\" /></p>\r\n\r\n<p>Tất cả đ&oacute; gi&uacute;p Galaxy A50 cho ra những bức ảnh c&oacute; chất lượng như được chụp bởi những nhiếp ảnh gia chuy&ecirc;n nghiệp. Bất kể thời gian v&agrave; kh&ocirc;ng gian, h&agrave;nh tr&igrave;nh của bạn sẽ trở n&ecirc;n trọn vẹn với cụm 3 camera sẵn s&agrave;ng bắt trọn mọi n&eacute;t đẹp d&ugrave; trong điều kiện thiếu s&aacute;ng.&nbsp;</p>\r\n\r\n<p>Đặc bi&ecirc;t với t&iacute;nh năng nhận diện ảnh lỗi khi Galaxy A50 c&oacute; thể ph&acirc;n biệt được c&aacute;c trường hợp mắt nhắm, ống k&iacute;nh bị mờ hay ngược s&aacute;ng. Nhờ đ&oacute; người d&ugrave;ng c&oacute; thể kịp thời điều chỉnh v&agrave; cho ra những khoảnh khắc ho&agrave;n hảo v&agrave; sắc n&eacute;t nhất.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/samsung/Galaxy-A50-3.jpg\" style=\"height:446px; width:840px\" /></p>\r\n\r\n<h3><strong>Mở kh&oacute;a nhanh v&agrave; bảo mật an to&agrave;n với x&aacute;c thực sinh trắc học</strong></h3>\r\n\r\n<p>Galaxy A50 với&nbsp; c&ocirc;ng nghệ bảo mật sinh trắc học t&acirc;n tiến với khả năng qu&eacute;t v&acirc;n tay&nbsp; dưới m&agrave;n h&igrave;nh nhanh ch&oacute;ng v&agrave; an to&agrave;n. Ngo&agrave;i ra, bạn cũng c&oacute; thể lựa chọn một phương thức kh&aacute;c v&agrave; nhanh ch&oacute;ng kh&ocirc;ng k&eacute;m đ&oacute; ch&iacute;nh l&agrave; nhận diện khu&ocirc;n mặt. Việc bạn cần l&agrave;m l&agrave; giữ điện thoại trước mặt, c&ocirc;ng nghệ nhận diện khu&ocirc;n mặt sẽ gi&uacute;p bạn truy cập v&agrave;o điện thoại một c&aacute;ch nhanh ch&oacute;ng nhưng kh&ocirc;ng k&eacute;m phần an to&agrave;n.</p>\r\n', 2, 368, 11, 10, 6990000, 6390000, '2019-06-03 21:49:23', '1', '2021-06-27 17:04:43', '1', 0, 1),
(6, 8, 'Redmi Note 7 32GB Pink', 'redmi-note-7-32gb-pink', 'dff47577347f3fddcda326afac0c7f34.jpg', '467d27927f4a8057bcf3dd2bb7fe7ef4.jpg#06488aaef1d138e77c10e4c750305f1c.jpg', 'Cấu hình đỉnh giá bất ngờ', '<h2><a href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-7\" target=\"_blank\">Xiaomi Redmi Note 7</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">smartphone</a>&nbsp;gi&aacute; rẻ mới được giới thiệu v&agrave;o đầu năm 2019 với nhiều trang bị đ&aacute;ng gi&aacute; như&nbsp;thiết kế notch giọt nước hay camera l&ecirc;n tới 48 MP.</h2>\r\n\r\n<h3>Hiệu năng mạnh mẽ trải nghiệm game mượt m&agrave;</h3>\r\n\r\n<p>Redmi Note 7 xứng đ&aacute;ng l&agrave; một trong những chiếc smartphone c&oacute; hiệu năng tốt, với điểm Antutu đo được khoảng 137586.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/Slider/vi-vn-xiaomi-redmi-note-7-thietke.jpg\" style=\"height:433px; width:780px\" />Nhờ được cung cấp sức mạnh bởi con chip Snapdragon 660 c&ugrave;ng RAM 4 GB, m&aacute;y chiến mượt m&agrave; c&aacute;c d&ograve;ng game hiện n&agrave;y với cấu h&igrave;nh tầm trung hoặc cao (tuỳ game) như Li&ecirc;n Qu&acirc;n, PUBG,...</p>\r\n\r\n<p>Giao diện trực quan dễ sử dụng, c&oacute; thể tuỳ chỉnh ph&iacute;m điều hướng cũng l&agrave; một ưu điểm của m&aacute;y.</p>\r\n\r\n<h3>Thiết kế hiện đại, theo xu thế</h3>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\">điện thoại Xiaomi</a>&nbsp;mới l&agrave; một sự lột x&aacute;c ho&agrave;n to&agrave;n về thiết kế so với c&aacute;c d&ograve;ng Redmi trước đ&acirc;y.<img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-4.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p>M&aacute;y sở hữu cho m&igrave;nh&nbsp;mặt lưng bằng kính ph&ocirc;́i màu gradient, mang lại cho m&aacute;y cảm gi&aacute;c cao cấp v&agrave; sang trọng hơn rất nhiều.</p>\r\n\r\n<p>4 g&oacute;c m&aacute;y được tăng cường độ bền đ&aacute;ng kể gi&uacute;p chống vỡ k&iacute;nh gấp 4 lần so với c&aacute;c smartphone kh&aacute;c tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh giọt nước th&iacute;ch mắt</h3>\r\n\r\n<p>Xiaomi Redmi Note 7 sở hữu m&agrave;n h&igrave;nh 6.3 inch của c&oacute; tỷ lệ khung h&igrave;nh 19.5:9,&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/do-phan-giai-man-hinh-qhd-hd-fullhd-2k-4k-la-gi--592178#fullhd\" target=\"_blank\">độ ph&acirc;n giải Full HD+</a>(2.340 x 1.080 pixel).</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn.tgdd.vn/Products/Images/42/167535/xiaomi-redmi-note-7-tgdd-1.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<h3>Camera chất lượng tốt trong tầm gi&aacute;</h3>\r\n\r\n<p>Xiaomi Redmi Note 7 trở th&agrave;nh chiếc điện thoại c&oacute; camera &quot;khủng&quot; gi&aacute; rẻ nhất tr&ecirc;n thị trường hiện nay với &quot;số chấm&quot; l&ecirc;n tới&nbsp;&nbsp;48 MP.</p>\r\n', 1, 420, 10, 10, 4990000, 4550000, '2019-06-03 21:54:51', '1', '2021-06-27 16:59:20', '1', 0, 1),
(7, 1, 'Apple iPhone X 64GB White  Chính hãng', 'apple-iphone-x-64gb-white-chinh-hang', '636483223586180190_1.jpg', '49159254e59c88f0fd2cc7a61dcc45c8.jpg', 'iPhone X 64GB – Siêu phẩm kỷ niệm 10 năm của Apple', '<p><em>Sản phẩm kỷ niệm 10 năm ra mắt của Apple n&ecirc;n&nbsp;<strong>iPhone X 64GB ch&iacute;nh h&atilde;ng VN/A</strong>&nbsp;c&oacute; sự thay đổi so với bộ đ&ocirc;i&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple/iphone-8-8-plus.html\" target=\"_blank\">iPhone 8, 8 Plus</a>&nbsp;trước đ&oacute; từ t&iacute;nh năng đến thiết kế khiến nhiều người d&ugrave;ng smartphone khao kh&aacute;t. Ngo&agrave;i ra, bạn c&oacute; thể tham khảo phi&ecirc;n bản dung lượng bộ nhớ trong cao hơn l&agrave;&nbsp;<strong><a href=\"https://cellphones.com.vn/apple-iphone-x-256-gb-chinh-hang-vn.html\" target=\"_blank\">iPhone X 256GB</a></strong></em></p>\r\n\r\n<h3><strong>iPhone m&atilde; VN/A l&agrave; g&igrave;? V&igrave; sao n&ecirc;n chọn iPhone X 64GB ch&iacute;nh h&atilde;ng VN/A?</strong></h3>\r\n\r\n<p>Trước khi ra mắt mỗi sản phẩm cho từng thị trường th&igrave; Apple đ&atilde; nghi&ecirc;n cứu rất kỹ lưỡng để ph&ugrave; hợp nhất với thị trường đ&oacute;. V&iacute; dụ như với Nhật Bản v&agrave; H&agrave;n Quốc đ&oacute; l&agrave; cấm chụp l&eacute;n n&ecirc;n smartphone của 2 thị trường n&agrave;y sẽ kh&ocirc;ng tắt được &acirc;m chụp h&igrave;nh. V&agrave; điện thoại iPhone tại thị trường Việt Nam cũng sẽ như vậy, Apple đ&atilde; nghi&ecirc;n cứu những th&oacute;i quen, quy định của Việt Nam để sản xuất ra iPhone X 64GB ch&iacute;nh h&atilde;ng m&atilde; VN/A.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/apple/iPhone-X-64GB-chinh-hang-VNA-1.jpg\" style=\"height:586px; width:840px\" /></p>\r\n\r\n<p>Vậy cụ thể iPhone m&atilde; VA/A l&agrave; g&igrave;? iPhone m&atilde; VN/A l&agrave; h&agrave;nh do ch&iacute;nh Apple sản xuất cho từng thị trường cụ thể v&agrave; đảm bảo chất lượng, kiểm duyệt của Apple. N&ecirc;n khi mua<em>&nbsp;iPhone X 64GB ch&iacute;nh h&atilde;ng m&atilde; VN/A</em>&nbsp;kh&aacute;ch h&agrave;ng sẽ được hưởng nhiều ưu đ&atilde;i hơn so với iPhone cũ, iPhone x&aacute;ch tay. Đ&oacute; l&agrave; ch&iacute;nh s&aacute;ch bảo h&agrave;nh hấp dẫn, nguồn gốc r&otilde; r&agrave;ng, sản phẩm đầy phụ kiện. Đ&acirc;y l&agrave; những điều m&agrave; iPhone X cũ sẽ kh&ocirc;ng thể c&oacute; hoặc c&oacute; kh&ocirc;ng đầy đủ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/apple/iPhone-X-64GB-chinh-hang-VNA-2.jpg\" style=\"height:560px; width:840px\" /></p>\r\n\r\n<p>So với những năm trước th&igrave; Apple từng sản xuất những mẫu smartphone c&oacute; dung lượng chỉ 16GB, 32GB th&igrave; 64GB đ&atilde; l&agrave; một dung lượng lớn, đủ để đ&aacute;p ứng mọi nhu cầu sử dụng của kh&aacute;ch h&agrave;ng. Được chạy tr&ecirc;n một hệ điều h&agrave;nh iOS đ&atilde; được tối ưu h&oacute;a, iPhone X 64GB cho ph&eacute;p người d&ugrave;ng thoải m&aacute;i lưu trữ &acirc;m nhạc, h&igrave;nh ảnh, video hay tải c&aacute;c ứng dụng m&agrave; kh&ocirc;ng lo đầy bộ nhớ.</p>\r\n\r\n<h3><strong>iPhone X 64GB thiết kế đột ph&aacute; với m&agrave;n h&igrave;nh tai thỏ</strong></h3>\r\n\r\n<p>Sau 4 năm trung th&agrave;nh với một kiểu thiết kế, bắt đầu từ iPhone 6/ 6 Plus đến&nbsp;<a href=\"https://cellphones.com.vn/mobile/apple/iphone-8-8-plus.html\" target=\"_blank\">iPhone 8/8 Plus</a>, th&igrave; m&atilde;i đến iPhone X Apple mới c&oacute; một sự thay đổi ho&agrave;n to&agrave;n trong thiết kế của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/apple/iPhone-X-64GB-chinh-hang-VNA-5.jpg\" style=\"height:466px; width:840px\" /></p>\r\n\r\n<p>Face ID hoạt động bằng c&aacute;ch sử dụng camera hồng ngoại, m&ocirc;̣t đ&egrave;n chi&ecirc;́u v&agrave; cảm bi&ecirc;́n ch&acirc;́m với hơn 30.000 điểm kh&ocirc;ng nh&igrave;n thấy được để x&acirc;y dựng v&agrave; đ&ocirc;́i chi&ecirc;́u t&acirc;́t cả đường n&eacute;t tr&ecirc;n khu&ocirc;n mặt của người d&ugrave;ng mang lại khả năng nhận diện khu&ocirc;n mặt cực k&igrave; chuẩn x&aacute;c. D&ugrave; người d&ugrave;ng c&oacute; cạo đi r&acirc;u, thay đổi kiểu t&oacute;c th&igrave; Face ID cũng dễ d&agrave;ng nhận ra v&agrave; mở kh&oacute;a iPhone một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>Vi xử l&yacute;&nbsp;<a href=\"https://cellphones.com.vn/sforum/chipset-apple-a11-bionic-tren-iphone-8-va-iphone-x-danh-bai-tat-ca-cac-thiet-bi-android-voi-diem-geekbench-cao-ngat-nguong\" target=\"_blank\">Apple A11 Bionic</a>&nbsp;đem lại những trải nghiệm d&ugrave;ng tuyệt vời</strong></h3>\r\n\r\n<p>Hiệu năng l&agrave; điều chưa bao giờ phải b&agrave;n c&atilde;i với c&aacute;c sản phẩm từ Apple v&agrave;&nbsp;<em>iPhone X 64Gb</em>&nbsp;cũng kh&ocirc;ng ngoại lệ.&nbsp;<strong>iPhone X 64GB</strong>&nbsp;được trang bị chip xử l&yacute; A11 Bionic s&aacute;u l&otilde;i (Hexa core) 64 bit đem lại tốc độ nhanh hơn 25% so với chip A10, tiết kiệm năng lượng l&ecirc;n tới 70%.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/apple/iPhone-X-64GB-chinh-hang-VNA-6.jpg\" style=\"height:466px; width:840px\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;&nbsp;<a href=\"https://www.notebookcheck.net/Apple-A11-Bionic-GPU-GPU-Benchmarks-and-Specs.250994.0.html\" target=\"_blank\">chip xử l&yacute; đồ họa GPU M11</a>&nbsp;cũng gi&uacute;p cải thiện tốc độ l&ecirc;n khoảng 30% so với GPU cũ. Nhờ vậy, người d&ugrave;ng c&oacute; thể lướt game nhanh v&agrave; mượt hơn.</p>\r\n\r\n<h3><strong>Mua iPhone X 64GB ch&iacute;nh h&atilde;ng VN/A gi&aacute; tốt tại CellphoneS</strong></h3>\r\n\r\n<p>Smartphone&nbsp;<em><strong>iPhone X 64GB ch&iacute;nh h&atilde;ng</strong></em>&nbsp;l&agrave; một flagship rất đ&aacute;ng để trải nghiệm. Hiện sản phẩm đang được b&aacute;n ch&iacute;nh h&atilde;ng tại hệ thống cửa h&agrave;ng của CellphoneS,nếu bạn quan t&acirc;m đến sản phẩm n&agrave;y th&igrave; h&atilde;y đến c&aacute;c hệ thống cửa h&agrave;ng&nbsp;<a href=\"https://cellphones.com.vn/\" target=\"_blank\">CellphoneS&nbsp;</a>để được trải nghiệm tận tay. Ngo&agrave;i ra, khi mua iPhone X 64GB ch&iacute;nh h&atilde;ng kh&aacute;ch h&agrave;ng sẽ được hưởng nhiều ch&iacute;nh s&aacute;ch ưu đ&atilde;i mua h&agrave;ng trả g&oacute;p với thẻ t&iacute;n dụng, bảo h&agrave;nh 12 th&aacute;ng ch&iacute;nh h&atilde;ng, 1 đổi 1 trong 30 ng&agrave;y, miễn ph&iacute; giao h&agrave;ng, thu tiền tại nh&agrave;.</p>\r\n', 2, 190, 20, 0, 21900000, 21900000, '2019-06-03 22:04:43', '1', '2021-06-27 16:57:33', '1', 0, 1),
(8, 1, 'iPhone Xs Max 64GB  Rose gold', 'iphone-xs-max-64gb-rose-gold', '636748771945393060_iPhone-Xs-Max-gold.jpg', 'eb94d71b8618424db4dafbb58b39ee25.jpg#8161dd6861975d560bc1d272ed332243.jpg#a1505c545351e44c877c2edf96ebecbf.jpg', 'iPhone Xs Max 64GB Rose gold (Vàng hồng) là chiếc iPhone có màn hình lớn nhất từ trước đến nay, mang đến những trải nghiệm tuyệt vời về màn hình và thời lượng pin.', '<h2><strong>M&agrave;n h&igrave;nh lớn 6,5 inch chuẩn Super Retina si&ecirc;u sắc n&eacute;t</strong></h2>\r\n\r\n<p><strong>iPhone Xs Max 64GB</strong>&nbsp;c&oacute; m&agrave;n h&igrave;nh vượt trội so với mọi thế hệ iPhone trước đ&acirc;y khi lớn tới 6,5 inch, được l&agrave;m từ tấm nền OLED, hỗ trợ hiển thị HDR. HDR mang đến m&agrave;u sắc ch&iacute;nh x&aacute;c, m&agrave;u đen c&oacute; chiều s&acirc;u, độ s&aacute;ng v&agrave; độ tương phản tuyệt vời. Bạn sẽ được tận hưởng một m&agrave;n h&igrave;nh cực lớn, độ ph&acirc;n giải si&ecirc;u cao 2688 x 1242 pixels cho trải nghiệm xem đ&aacute;ng kinh ngạc.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-xs-max-3.jpg\" style=\"height:455px; width:907px\" /></p>\r\n\r\n<p>Nếu như bạn lo lắng m&agrave;n h&igrave;nh lớn sẽ khiến cho&nbsp;<strong>iPhone Xs Max 64GB&nbsp;</strong>kh&oacute; cầm nắm tr&ecirc;n tay th&igrave; h&atilde;y qu&ecirc;n ngay nỗi lo đ&oacute;, v&igrave; viền m&agrave;n h&igrave;nh tr&agrave;n 4 cạnh gi&uacute;p cho iPhone Xs Max thậm ch&iacute; c&ograve;n nhỏ gọn hơn iPhone 8 Plus. Ch&uacute;ng ta c&oacute; một chiếc m&aacute;y m&agrave;n h&igrave;nh lớn hơn rất nhiều, nhưng vẫn đủ gọn g&agrave;ng để dễ d&agrave;ng cầm nắm v&agrave; thao t&aacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-xs-max-2.JPG\" style=\"height:654px; width:981px\" /></p>\r\n\r\n<h2><strong>Sự kết hợp giữa k&iacute;nh v&agrave; th&eacute;p, sang trọng nhưng kh&ocirc;ng k&eacute;m phần bền vững</strong></h2>\r\n\r\n<p><strong>iPhone Xs Max 64GB</strong>&nbsp;được ho&agrave;n thiện từ những vật liệu đặc biệt, đ&oacute; l&agrave; viền th&eacute;p kh&ocirc;ng gỉ s&aacute;ng b&oacute;ng v&agrave; mặt k&iacute;nh bền nhất trong thế giới smartphone. Kỹ thuật gia c&ocirc;ng ch&iacute;nh x&aacute;c ho&agrave;n hảo của Apple mang đến chiếc điện thoại cứng c&aacute;p, thời trang, vừa vặn trong l&ograve;ng b&agrave;n tay bạn. Ba m&agrave;u sắc thời thượng X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; V&agrave;ng cho những lựa chọn c&aacute; t&iacute;nh. Đặc biệt, hai phi&ecirc;n bản m&agrave;u V&agrave;ng v&agrave; X&aacute;m được sơn bằng qu&aacute; tr&igrave;nh xử l&yacute; m&agrave;u PVD, tạo hiệu ứng &aacute;nh s&aacute;ng qua lớp k&iacute;nh rất đẹp mắt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xs-3.jpg\" style=\"height:700px; width:1050px\" /></p>\r\n\r\n<h2><strong>Face ID thế hệ mới, mật khẩu ch&iacute;nh l&agrave; khu&ocirc;n mặt của bạn</strong></h2>\r\n\r\n<p>Face ID tr&ecirc;n&nbsp;<strong>iPhone Xs Max 64GB</strong>&nbsp;khiến cho việc bảo mật đơn giản v&agrave; an to&agrave;n hơn. Bạn c&oacute; thể mở kh&oacute;a, đăng nhập, thanh to&aacute;n nhanh ch&oacute;ng th&ocirc;ng qua nhận diện khu&ocirc;n mặt. Ngay khi đưa m&aacute;y l&ecirc;n, hệ thống camera TrueDepth sẽ nhận diện tới 30.000 điểm tr&ecirc;n khu&ocirc;n mặt của bạn v&agrave; mở kh&oacute;a ngay lập tức. Đồng thời, hệ thống n&agrave;y cũng sẽ ghi nhớ v&agrave; học hỏi th&oacute;i quen của bạn để sau n&agrave;y d&ugrave; c&oacute; đội mũ hay đeo k&iacute;nh th&igrave; Face ID vẫn nhận diện nhanh ch&oacute;ng đầy ch&iacute;nh x&aacute;c. Face ID của iPhone Xs Max ch&iacute;nh l&agrave; phương thức bảo mật an to&agrave;n nhất tr&ecirc;n smartphone từ trước đến nay.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xs-4.jpg\" style=\"height:812px; width:1217px\" /></p>\r\n', 2, 160, 33, 0, 28900000, 28900000, '2019-06-03 22:08:30', '1', '2021-06-27 16:55:46', '1', 0, 1),
(9, 7, 'Huawei Mate 20 Pro (Xanh dương)', 'huawei-mate-20-pro-xanh-duong', '636753320259321737_huawei-mate20-pro-xanhla-1.jpg', 'cf69d7775e92df0125cf38b8dc38e697.jpg', 'Huawei Mate 20 Pro chính hãng - Thông tin giá bán, cấu hình chi tiết, quà tặng khủng', '<p>Huawei đang dần chứng minh với thế giới rằng họ l&agrave; c&ocirc;ng ty c&ocirc;ng nghệ chuy&ecirc;n sản xuất smartphone chất lượng cao nắm trong tay gần 10% thị phần smartphone to&agrave;n thế giới.&nbsp;Điện thoại&nbsp;<strong>Huawei Mate 20 Pro</strong>&nbsp;tiếp tục được&nbsp;<a href=\"https://cellphones.com.vn/mobile/huawei.html\" target=\"_blank\">Huawei</a>&nbsp;cho ra đời nhằm củng cố điều đ&oacute;.</p>\r\n\r\n<h2>Thiết kế Huawei Mate 20 Pro đẳng cấp</h2>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/huawei/huawei-mate-20-pro-chinh-hang-1_1.png\" style=\"height:600px; width:640px\" /></p>\r\n\r\n<p><strong>Huawei Mate 20 Pro</strong>&nbsp;v&agrave;&nbsp;<em><a href=\"https://cellphones.com.vn/huawei-mate-20-chinh-hang.html\" target=\"_blank\">Huawei Mate 20</a></em>&nbsp;đều sở hữu thiết kế hai mặt k&iacute;nh b&oacute;ng bẩy tạo vẻ đẹp tinh tế v&agrave; bắt mắt với bất cứ ai nh&igrave;n v&agrave;o. B&ecirc;n cạnh đ&oacute;, kiểu d&aacute;ng vu&ocirc;ng vắn hiện đại v&agrave; những đường bo cong hợp l&yacute; để tạo cảm gi&aacute;c cầm nắm chắc chắn. Đặc biệt, với thiết kế cong 2 b&ecirc;n giống như tr&ecirc;n chiếc Mate RS Porsche Design hay giống c&aacute;c thiết bị Galaxy d&ograve;ng S mới đến từ Samsung gi&uacute;p cho m&aacute;y dễ d&agrave;ng nằm gọn trong tay người d&ugrave;ng.</p>\r\n\r\n<h2>M&agrave;n h&igrave;nh Mate 20 Pro tr&agrave;n viền sắc n&eacute;t</h2>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/huawei/huawei-mate-20-pro-chinh-hang-4_1.png\" style=\"height:600px; width:640px\" /></p>\r\n\r\n<p><strong>Mate 20 Pro</strong>&nbsp;sở hữu m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước lớn 6.39 inches c&ugrave;ng độ ph&acirc;n giải&nbsp;2K+ 3120x1440 cho khả năng hiển thị ngo&agrave;i trời v&agrave; g&oacute;c nh&igrave;n cực tốt. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;OLED đem lại h&igrave;nh ảnh v&agrave; video được t&aacute;i tạo một c&aacute;ch ch&acirc;n thực, sắc n&eacute;t với độ s&aacute;ng, độ tương phản cao ở từng chi tiết, thỏa m&atilde;n nhu cầu giải tr&iacute; của người d&ugrave;ng.</p>\r\n\r\n<h2>Cấu h&igrave;nh Huawei Mate 20 Pro ch&iacute;nh h&atilde;ng mạnh mẽ</h2>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/huawei/huawei-mate-20-pro-chinh-hang-6.png\" style=\"height:453px; width:640px\" /></p>\r\n\r\n<p><strong>Huawei Mate 20 Pro</strong>&nbsp;<strong>ch&iacute;nh h&atilde;ng</strong>&nbsp;được trang bị chip mới l&agrave; Kirin 980 do ch&iacute;nh Huawei tự sản xuất tr&ecirc;n tiến tr&igrave;nh 7nm c&oacute; NPU d&agrave;nh ri&ecirc;ng cho tr&iacute; th&ocirc;ng minh nh&acirc;n tạo đem lại khả năng xử l&yacute; t&aacute;c vụ năng v&ocirc; c&ugrave;ng mượt m&agrave; v&agrave; ổn định.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, RAM 6 GB thoải m&aacute;i cho bạn c&ugrave;ng bộ nhớ trong 128GB gi&uacute;p bạn lưu trữ dữ liệu thỏa th&iacute;ch.&nbsp;<strong>Mate 20 Pro</strong>&nbsp;được chạy tr&ecirc;n nền hệ điều h&agrave;nh Android 9.</p>\r\n\r\n<h2>Camera Huawei Mate 20 Pro ch&iacute;nh h&atilde;ng bắt trọn từng khoảnh khắc</h2>\r\n\r\n<p><img alt=\"\" src=\"https://cellphones.com.vn/media/wysiwyg/mobile/huawei/huawei-mate-20-pro-chinh-hang-3_1.png\" style=\"height:600px; width:640px\" /></p>\r\n\r\n<p><strong>Mate 20 Pro</strong>&nbsp;Ch&iacute;nh h&atilde;ng được trang bị ba camera trang bị AI bao gồm camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 40MP v&agrave; hai camera phụ c&oacute; độ ph&acirc;n giải 20MP đơn sắc + 8MP tele hỗ trợ c&ocirc;ng nghệ AI, nhận dạng được nhiều bối cảnh kh&aacute;c nhau như phong cảnh, đồ ăn... gi&uacute;p cho camera c&oacute; thể bắt được chủ thể khu&ocirc;n h&igrave;nh v&agrave; l&agrave;m mờ hậu cảnh một c&aacute;ch ch&iacute;nh x&aacute;c. C&ograve;n camera trước của m&aacute;y c&oacute; độ ph&acirc;n giải l&ecirc;n đến 24MP gi&uacute;p người d&ugrave;ng c&oacute; những bức ảnh tự sướng ảo diệu, tự tin chia sẻ cho bạn b&egrave;.</p>\r\n\r\n<h2>Thời lượng pin Mate 20 Pro ch&iacute;nh h&atilde;ng ấn tượng</h2>\r\n\r\n<p><strong>Huawei Mate 20 Pro</strong>&nbsp;ch&iacute;nh h&atilde;ng sở hữu pin với dung lượng 4.200 mAh, con chip tiết kiệm pin cho thời gian sử dụng l&agrave; dư dả trong một ng&agrave;y. Ngo&agrave;i ra,&nbsp;<strong>Mate 20 Pro&nbsp;</strong>c&ograve;n được hỗ trợ c&ocirc;ng nghệ sạc nhanh cho ph&eacute;p điện thoại tăng 50% tốc độ chỉ trong 25 ph&uacute;t gi&uacute;p cho người d&ugrave;ng mau ch&oacute;ng sử dụng m&aacute;y khi cần thiết. Ngo&agrave;i ra, Huawei Mate 20 Pro c&ograve;n hỗ trợ sạc ngược cho c&aacute;c thiết bị được trang bị c&ocirc;ng nghệ NFC.</p>\r\n\r\n<p>Mua&nbsp;<a href=\"https://cellphones.com.vn/huawei-mate-20-pro-chinh-hang.html\" target=\"_blank\"><em>Huawei Mate 20 Pro Ch&iacute;nh h&atilde;ng gi&aacute; bao nhi&ecirc;u</em></a>, chế độ bảo h&agrave;nh như thế n&agrave;o? H&atilde;y đến với CellphoneS, hệ thống b&aacute;n lẻ điện thoại tr&ecirc;n to&agrave;n quốc hiện sẽ cung cấp cho kh&aacute;ch h&agrave;ng sản phẩm Huawei&nbsp;<strong><em>Mate 20 Pro</em></strong>&nbsp;ch&iacute;nh h&atilde;ng trong thời gian sớm nhất khi được nh&agrave; sản xuất ra mắt c&ugrave;ng với việc b&aacute;n mức gi&aacute; v&ocirc; c&ugrave;ng hấp dẫn, chế độ hậu m&atilde;i v&ocirc; c&ugrave;ng tốt. Đối với c&aacute;c kh&aacute;ch h&agrave;ng ở xa c&oacute; nhu cầu mua sản phẩm c&oacute; thể tham khảo qua dịch vụ mua h&agrave;ng v&agrave; thanh to&aacute;n tận nơi miễn ph&iacute; của CellphoneS. Đặc biệt, đối với những sản phẩm mới ra mắt, kh&aacute;ch h&agrave;ng c&oacute; thể đặt cọc online để ưu ti&ecirc;n nhận m&aacute;y v&agrave; sở hữu nhiều phần qu&agrave; hấp dẫn.</p>\r\n', 2, 220, 30, 10, 16990000, 15990000, '2019-06-03 22:14:17', '1', '2021-06-27 17:14:06', '1', 0, 1);
